targets.query:
from(bucket: v.bucket)
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r._measurement == "system" and r._field == "load5" and r.host == "${server}")

from(bucket: v.bucket)
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r._measurement == "system" and r.host == "${server}" and contains(value: r._field, set: ["load1", "load5", "load15"]))

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r.host == "${server}" and r._measurement == "linux_sysctl_fs" and r._field =~ /file-.*/ )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r.host == "${server}" and r._measurement == "conntrack" and r._field == "ip_conntrack_count" )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r.host == "${server}" and r._measurement == "conntrack" and r._field == "ip_conntrack_max" )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r.host == "${server}" and r._measurement == "netstat" and contains(value: r._field, set: ["tcp_close", "tcp_close_wait", "tcp_closing", "tcp_established", "tcp_fin_wait1", "tcp_fin_wait2", "tcp_last_ack", "tcp_syn_recv", "tcp_syn_sent", "tcp_time_wait"]) )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r.host == "${server}" and r.path == "${mountpoint}" and r._measurement == "disk" and contains(value: r._field, set: ["used", "total"] ) )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r.host == "${server}" and r.path == "${mountpoint}" and r._measurement == "disk" and r._field =~ /inodes_.*/ )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "internal_write" and r._field == "write_time_ns" )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "internal_gather" and r._field == "gather_time_ns" )

from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "internal_agent" and r._field == "metrics_written" )
